// Enhanced input processing with attachment detection
const body = $json.body || {};
const text = body.text || '';
const attachments = body.attachments || [];
const conversationId = body.conversation?.id || 'default';
const userId = body.from?.id || 'anonymous';

// Process attachments
const processedAttachments = attachments.map(att => ({
  name: att.name || 'unknown',
  contentType: att.contentType || 'application/octet-stream',
  contentUrl: att.contentUrl || '',
  content: att.content || ''
}));

// Extract mentions and commands
const mentions = text.match(/@\w+/g) || [];
const hasUrgent = /urgent|asap|priority|important/i.test(text);
const hasSchedule = /schedule|meeting|calendar|tomorrow|today|next week/i.test(text);

return {
  json: {
    originalText: text,
    cleanText: text.replace(/<[^>]*>/g, '').trim(),
    conversationId,
    userId,
    hasAttachments: processedAttachments.length > 0,
    attachments: processedAttachments,
    mentions,
    hasUrgent,
    hasSchedule,
    timestamp: new Date().toISOString(),
    context: {
      previousMessages: [], // Will be populated by memory system
      userProfile: {},
      preferences: {}
    }
  }
};

// Simple in-memory conversation storage (in production, use Redis/DB)
