// Generate adaptive card based on action type
function generateAdaptiveCard(action, data, response) {
  const baseCard = {
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "type": "AdaptiveCard",
    "version": "1.4",
    "body": []
  };

  // Header with icon and title
  const getHeaderConfig = (action) => {
    const configs = {
      'email_status': { icon: 'üìß', title: 'Email Status', color: 'Accent' },
      'calendar_view': { icon: 'üìÖ', title: 'Calendar Overview', color: 'Good' },
      'draft_email': { icon: '‚úèÔ∏è', title: 'Email Draft', color: 'Warning' },
      'send_email': { icon: 'üì§', title: 'Email Sent', color: 'Good' },
      'schedule_meeting': { icon: 'üóìÔ∏è', title: 'Meeting Scheduled', color: 'Good' },
      'error': { icon: '‚ùå', title: 'Error', color: 'Attention' },
      'help': { icon: 'ü§ñ', title: 'Teams-Outlook Assistant', color: 'Accent' }
    };
    return configs[action] || configs.help;
  };

  const headerConfig = getHeaderConfig(action);

  baseCard.body.push({
    "type": "TextBlock",
    "text": `${headerConfig.icon} ${headerConfig.title}`,
    "weight": "Bolder",
    "size": "Large",
    "color": headerConfig.color
  });

  // Main response text
  baseCard.body.push({
    "type": "TextBlock",
    "text": response,
    "wrap": true,
    "spacing": "Medium"
  });

  // Action-specific content
  if (action === 'email_status' && data && data.emails) {
    const emailCount = data.emails.length;
    const urgentCount = data.emails.filter(e => e.importance === 'high').length;

    baseCard.body.push({
      "type": "FactSet",
      "facts": [
        { "title": "üì¨ Total Unread:", "value": emailCount.toString() },
        { "title": "üî• Urgent:", "value": urgentCount.toString() },
        { "title": "üìä Last Check:", "value": new Date().toLocaleTimeString() }
      ]
    });

    // Show top 3 emails
    if (emailCount > 0) {
      baseCard.body.push({
        "type": "TextBlock",
        "text": "Recent Emails:",
        "weight": "Bolder",
        "spacing": "Large"
      });

      data.emails.slice(0, 3).forEach(email => {
        baseCard.body.push({
          "type": "TextBlock",
          "text": `üìß **${email.subject}**\n${email.from?.emailAddress?.name || 'Unknown'} ‚Ä¢ ${new Date(email.receivedDateTime).toLocaleString()}\n${email.bodyPreview.substring(0, 100)}...`,
          "wrap": true,
          "spacing": "Small"
        });
      });
    }
  }

  if (action === 'calendar_view' && data && data.events) {
    const eventCount = data.events.length;

    baseCard.body.push({
      "type": "FactSet",
      "facts": [
        { "title": "üìÖ Today's Events:", "value": eventCount.toString() },
        { "title": "‚è∞ Next Event:", "value": eventCount > 0 ? new Date(data.events[0].start.dateTime).toLocaleTimeString() : 'None' }
      ]
    });

    // Show upcoming events
    if (eventCount > 0) {
      data.events.forEach(event => {
        const startTime = new Date(event.start.dateTime).toLocaleTimeString();
        const endTime = new Date(event.end.dateTime).toLocaleTimeString();

        baseCard.body.push({
          "type": "TextBlock",
          "text": `üóìÔ∏è **${event.subject}**\n${startTime} - ${endTime}${event.location?.displayName ? ` ‚Ä¢ ${event.location.displayName}` : ''}`,
          "wrap": true,
          "spacing": "Small"
        });
      });
    }
  }

  // Add interactive actions
  const actions = [];

  if (action === 'email_status') {
    actions.push(
      { "type": "Action.Submit", "title": "üîÑ Refresh", "data": { "action": "refresh_emails" } },
      { "type": "Action.Submit", "title": "‚úèÔ∏è Compose", "data": { "action": "compose_email" } }
    );
  }

  if (action === 'calendar_view') {
    actions.push(
      { "type": "Action.Submit", "title": "üîÑ Refresh", "data": { "action": "refresh_calendar" } },
      { "type": "Action.Submit", "title": "üìÖ Schedule", "data": { "action": "schedule_meeting" } }
    );
  }

  if (action === 'draft_email') {
    actions.push(
      { "type": "Action.Submit", "title": "üì§ Send", "data": { "action": "send_draft" } },
      { "type": "Action.Submit", "title": "‚úèÔ∏è Edit", "data": { "action": "edit_draft" } }
    );
  }

  // Always add help action
  actions.push({ "type": "Action.Submit", "title": "‚ùì Help", "data": { "action": "help" } });

  if (actions.length > 0) {
    baseCard.actions = actions;
  }

  return baseCard;
}

