// Generate the response
const adaptiveCard = generateAdaptiveCard(
  responseData.action,
  responseData.data,
  responseData.response
);

// Update conversation memory
const memoryUpdate = updateConversationMemory(
  originalInput.conversationId,
  originalInput.cleanText,
  responseData.response
);

return {
  json: {
    type: "message",
    attachments: [{
      contentType: "application/vnd.microsoft.card.adaptive",
      content: adaptiveCard
    }],
    conversationUpdate: memoryUpdate,
    processingTime: Date.now() - new Date(originalInput.timestamp).getTime(),
    success: true
  }
};

// Comprehensive error handling
const error = $json.error || $json;
const originalInput = $('Input Processor').item?.json || {};

// Classify error type
function getErrorType(error) {
  const errorString = error.toString().toLowerCase();

  if (errorString.includes('authentication') || errorString.includes('unauthorized')) {
    return 'auth';
  }
  if (errorString.includes('rate limit') || errorString.includes('quota')) {
    return 'rate_limit';
  }
  if (errorString.includes('network') || errorString.includes('timeout')) {
    return 'network';
  }
  if (errorString.includes('permission') || errorString.includes('forbidden')) {
    return 'permission';
  }
  return 'general';
}

const errorType = getErrorType(error);

// Generate user-friendly error messages
const errorMessages = {
  auth: {
    title: 'üîê Authentication Required',
    message: 'I need to reconnect to your Microsoft account. Please contact your administrator to refresh the authentication.',
    actions: [{ type: 'Action.Submit', title: 'üîÑ Retry', data: { action: 'retry' } }]
  },
  rate_limit: {
    title: '‚è≥ Service Busy',
    message: 'Microsoft services are currently busy. Please try again in a few moments.',
    actions: [{ type: 'Action.Submit', title: 'üîÑ Try Again', data: { action: 'retry' } }]
  },
  network: {
    title: 'üåê Connection Issue',
    message: 'I\'m having trouble connecting to Microsoft services. Please check your network connection.',
    actions: [{ type: 'Action.Submit', title: 'üîÑ Retry', data: { action: 'retry' } }]
  },
  permission: {
    title: 'üö´ Permission Denied',
    message: 'I don\'t have the necessary permissions to perform this action. Please contact your administrator.',
    actions: [{ type: 'Action.Submit', title: '‚ùì Help', data: { action: 'help' } }]
  },
  general: {
    title: '‚ö†Ô∏è Something Went Wrong',
    message: 'I encountered an unexpected issue. Please try rephrasing your request or contact support.',
    actions: [
      { type: 'Action.Submit', title: 'üîÑ Try Again', data: { action: 'retry' } },
      { type: 'Action.Submit', title: '‚ùì Help', data: { action: 'help' } }
    ]
  }
};

const errorConfig = errorMessages[errorType];

// Generate error adaptive card
const errorCard = {
  "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
  "type": "AdaptiveCard",
  "version": "1.4",
  "body": [
    {
      "type": "TextBlock",
      "text": errorConfig.title,
      "weight": "Bolder",
      "size": "Large",
      "color": "Attention"
    },
    {
      "type": "TextBlock",
      "text": errorConfig.message,
      "wrap": true,
      "spacing": "Medium"
    },
    {
      "type": "TextBlock",
      "text": `üí° **Suggestion:** You can try: "help" to see available commands, or "check my emails" to test the connection.`,
      "wrap": true,
      "spacing": "Medium",
      "color": "Good"
    }
  ],
  "actions": errorConfig.actions
};

// Log error for debugging (in production, send to logging service)
console.log('Error Details:', {
  type: errorType,
  original: error,
  timestamp: new Date().toISOString(),
  user: originalInput.userId,
  conversation: originalInput.conversationId,
  input: originalInput.cleanText
});

return {
  json: {
    type: "message",
    attachments: [{
      contentType: "application/vnd.microsoft.card.adaptive",
      content: errorCard
    }],
    error: true,
    errorType,
    timestamp: new Date().toISOString()
  }
};

// Enhanced conversation logging and analytics
