    responseType: response.attachments?.[0]?.content?.body?.[0]?.text?.includes('üìß') ? 'email' : 
                  response.attachments?.[0]?.content?.body?.[0]?.text?.includes('üìÖ') ? 'calendar' : 'other'
  },
  performance: {
    totalProcessingTime: response.processingTime,
    nodeExecutionTime: Date.now() - new Date(input.timestamp).getTime()
  }
};

// Simple analytics (in production, use proper analytics service)
const analytics = {
