    calendar: response.attachments?.[0]?.content?.body?.[0]?.text?.includes('üìÖ') ? 1 : 0,
    help: response.attachments?.[0]?.content?.body?.[0]?.text?.includes('ü§ñ') ? 1 : 0
  }
};

console.log('Conversation Log:', logEntry);
